mapper.select:
select *
  from (
    select rownum as rnum,SearchPublicWorkResult.*
    from ( 
    		select * 
			  from false_report
			 where ind_id = #{id}

		order by FAL_NO asc
    )  SearchPublicWorkResult

    where rownum <= #{endRow}

)

where rnum >= #{startRow}


select * 
  from false_report
 where fal_no=#{falNo}
   and ind_id=#{indId}

mapper.update:
update false_report
   set 
   fal_title=#{falTitle}, fal_content=#{falContent}, co_name=#{coName}, fal_odate=#{falOdate}, fal_category=#{falCategory}, fal_categorydetail=#{falCategorydetail}, co_id = #{coId} 
 where fal_no=#{falNo}
   and ind_id=#{indId}

mapper.select:
select count(*)
from false_report
where ind_id = #{indId}
