mapper.select:
select *
  from (
    select rownum as rnum,SearchPublicWorkResult.*
    from ( 
    		select * 
			  from public_work
			 where pub_no is not null

		order by pub_no desc
    )  SearchPublicWorkResult

    where rownum <= #{endRow}

)

where rnum >= #{startRow}

mapper.update:
	update public_work
  	   set viewcnt=viewcnt+1
	 where pub_no=#{pubNo}
